import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
    setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    });
    await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    });

    fetchConversations();
    fetchMessages(messages?.conversationId, messages?.receiver);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
    localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    let profilePicUrl = user?.profilePic;

    if (newProfilePic) {
      const formData = new FormData();
      formData.append('file', newProfilePic);
      formData.append('upload_preset', 'imarj7');

      try {
        const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
        profilePicUrl = res.data.secure_url;

        const updatedUser = {
          ...user,
          fullName: newName,
          profilePic: profilePicUrl,
        };

        await fetch(`http://localhost:8000/api/users/${user?.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedUser),
        });

        localStorage.setItem('user:detail', JSON.stringify(updatedUser));
        setUser(updatedUser);
        setShowEditProfile(false);
      } catch (error) {
        console.error('Error uploading or updating profile picture:', error);
      }
    } else {
      const updatedUser = {
        ...user,
        fullName: newName,
      };

      await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      });

      localStorage.setItem('user:detail', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowEditProfile(false);
    }
  };

  return (
    <div className="w-screen flex">
      <div className={`w-[25%] h-screen bg-secondary overflow-scroll ${showFriends ? 'hidden' : ''}`}>
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, user)}>
                      <div><img src={user?.profilePic || pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="User Profile" /></div>
                      <div className="ml-6">
                        <h3 className="text-lg text-white font-semibold">{user?.fullName}</h3>
                        <p className="text-sm text-white text-gray-600">{user?.email}</p>
                        {unseenCount > 0 && <div className="text-sm text-red-500">{unseenCount} new messages</div>}
                      </div>
                    </div>
                  </div>
                )) : <div className="text-center text-white text-lg font-semibold mt-[90%]">No Conversations</div>
            }
          </div>
        </div>
      </div>

      <div className={`w-[73%] h-screen ${showFriends ? 'hidden' : ''}`}>
        <div className="flex justify-between px-10 py-4 bg-primary">
          <div className="flex items-center">
            <img src={messages?.receiver?.profilePic || pic} className="w-[50px] h-[50px] rounded-full p-[2px] border border-secondary" alt="Receiver Profile" />
            <div className="ml-4">
              <h3 className="text-xl text-white">{messages?.receiver?.fullName}</h3>
              <p className="text-sm text-gray-400">{messages?.receiver?.email}</p>
            </div>
          </div>
          <button className="bg-secondary text-white px-4 py-2 rounded" onClick={() => setShowFriends(true)}>Show Friends</button>
        </div>
        <div className="w-full h-[80%] overflow-y-scroll px-10">
          <div className="flex flex-col mt-6">
            {messages?.messages?.map(({ user, message }, index) => (
              <div key={index} className={`mb-4 ${user?.id === user?.id ? 'self-end' : 'self-start'}`}>
                <div className={`bg-${user?.id === user?.id ? 'blue-500' : 'gray-300'} text-white p-4 rounded-lg`}>
                  <p>{message}</p>
                </div>
              </div>
            ))}
            <div ref={messageRef}></div>
          </div>
        </div>
        <div className="w-full px-10 py-4 bg-primary">
          <Input
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type a message..."
            className="w-full bg-secondary text-white p-4 rounded-lg"
          />
          <button onClick={sendMessage} className="bg-secondary text-white px-4 py-2 rounded ml-4">Send</button>
        </div>
      </div>

      {showEditProfile && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-8 rounded-lg w-[50%]">
            <h2 className="text-2xl font-semibold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-lg font-semibold mb-2">Full Name</label>
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded-lg"
                />
              </div>
              <div className="mb-4">
                <label className="block text-lg font-semibold mb-2">Profile Picture</label>
                <input
                  type="file"
                  onChange={(e) => setNewProfilePic(e.target.files[0])}
                  className="w-full p-2 border border-gray-300 rounded-lg"
                />
              </div>
              <div className="flex justify-end">
                <button type="button" onClick={() => setShowEditProfile(false)} className="bg-gray-400 text-white px-4 py-2 rounded mr-2">Cancel</button>
                <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Save Changes</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showFriends && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-8 rounded-lg w-[50%] overflow-y-auto max-h-[80%]">
            <h2 className="text-2xl font-semibold mb-4">Friends List</h2>
            <div className="space-y-4">
              {users.map(user => (
                <div key={user.id} className="flex items-center justify-between p-4 border border-gray-300 rounded-lg">
                  <div className="flex items-center">
                    <img src={user.profilePic || pic} className="w-[50px] h-[50px] rounded-full p-[2px] border border-primary" alt="Friend Profile" />
                    <div className="ml-4">
                      <h3 className="text-lg font-semibold">{user.fullName}</h3>
                      <p className="text-sm text-gray-600">{user.email}</p>
                    </div>
                  </div>
                  <button onClick={() => fetchMessages(user.conversationId, user)} className="bg-primary text-white px-4 py-2 rounded">Message</button>
                </div>
              ))}
            </div>
            <div className="flex justify-end mt-4">
              <button onClick={() => setShowFriends(false)} className="bg-gray-400 text-white px-4 py-2 rounded">Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
