import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for HTTP requests

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    // Filter out duplicates and initialize unseen counts
    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    // Store unique conversations in local storage
    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    // Load conversations from local storage if available
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    try {
      const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setMessages({ messages: resData, receiver, conversationId });
      setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
    } catch (error) {
      console.error('Failed to fetch messages:', error);
    }
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    try {
      socket?.emit('sendMessage', {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message,
        conversationId: messages?.conversationId
      });

      await fetch(`http://localhost:8000/api/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          conversationId: messages?.conversationId,
          senderId: user?.id,
          message,
          receiverId: messages?.receiver?.receiverId
        })
      });

      // Refresh the dashboard
      await fetchConversations();
      await fetchMessages(messages?.conversationId, messages?.receiver);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
    localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    let profilePicUrl = user?.profilePic;

    if (newProfilePic) {
      const formData = new FormData();
      formData.append('file', newProfilePic);
      formData.append('upload_preset', 'imarj7');

      try {
        const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
        profilePicUrl = res.data.secure_url;

        // Update user with new profile picture URL
        const updatedUser = {
          ...user,
          fullName: newName,
          profilePic: profilePicUrl,
        };

        // Update user info in backend (assuming there's an endpoint for that)
        await fetch(`http://localhost:8000/api/users/${user?.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedUser),
        });

        // Update local storage and state
        localStorage.setItem('user:detail', JSON.stringify(updatedUser));
        setUser(updatedUser);
        setShowEditProfile(false);
      } catch (error) {
        console.error('Error uploading or updating profile picture:', error);
      }
    } else {
      // Handle case where no new profile picture was selected
      const updatedUser = {
        ...user,
        fullName: newName,
      };

      // Update user info in backend (assuming there's an endpoint for that)
      await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      });

      // Update local storage and state
      localStorage.setItem('user:detail', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowEditProfile(false);
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, { receiverId: user.id, fullName: user.fullName, profilePic: user.profilePic })}>
                      <div className="relative"><img src={user.profilePic || pic} className="w-12 h-12 rounded-full" alt="User Profile" />
                        {
                          unseenCount > 0 && (
                            <div className="absolute bottom-1 right-1 bg-main w-6 h-6 rounded-full text-center text-white">
                              <span>{unseenCount}</span>
                            </div>
                          )
                        }
                      </div>
                      <div className="ml-4 text-md text-secondary font-semibold">{user.fullName}</div>
                    </div>
                  </div>
                )) : <div className="text-lg text-gray-400">No conversations available.</div>
            }
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10 mb-20">
          <div className="text-main text-lg font-semibold">Friends</div>
          <div className="flex items-center">
            <button onClick={() => setShowFriends(!showFriends)} className="cursor-pointer text-sm font-semibold text-blue-600">
              {showFriends ? 'Hide' : 'Show'} Friends
            </button>
          </div>
          {showFriends && (
            <div className="mt-6">
              {
                users.length > 0 ?
                  users.map((user) => (
                    <div className="flex items-center py-6 border-b border-b-gray-300" key={user.id}>
                      <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(user.id, { receiverId: user.id, fullName: user.fullName, profilePic: user.profilePic })}>
                        <img src={user.profilePic || pic} className="w-12 h-12 rounded-full" alt="User Profile" />
                        <div className="ml-4 text-md text-secondary font-semibold">{user.fullName}</div>
                      </div>
                    </div>
                  )) : <div className="text-lg text-gray-400">No friends found.</div>
              }
            </div>
          )}
        </div>
      </div>
      {/* Right Section */}
      <div className="w-[75%] h-screen bg-white flex flex-col justify-between">
        {/* Top Bar */}
        <div className="h-[10%] border-b border-b-gray-300">
          <div className="flex items-center p-4">
            <div className="w-12 h-12 rounded-full border border-main p-[2px]">
              <img src={messages?.receiver?.profilePic || pic} className="w-full h-full rounded-full" alt="Receiver Profile" />
            </div>
            <div className="ml-4">
              <h3 className="text-lg font-semibold">{messages?.receiver?.fullName || 'Select a Conversation'}</h3>
              {messages?.receiver && <p className="text-md text-gray-500">Online</p>}
            </div>
          </div>
        </div>
        {/* Messages */}
        <div className="flex-grow p-6 overflow-y-scroll">
          {messages?.messages?.map((msg, index) => (
            <div key={index} className={`flex ${msg.user.id === user.id ? 'justify-end' : 'justify-start'} my-2`}>
              <div className={`${msg.user.id === user.id ? 'bg-main text-white' : 'bg-gray-200 text-black'} rounded-lg p-4 max-w-[60%]`}>
                <p>{msg.message}</p>
              </div>
            </div>
          ))}
          <div ref={messageRef}></div>
        </div>
        {/* Input Box */}
        {messages?.receiver && (
          <div className="h-[10%] p-4 border-t border-t-gray-300 flex items-center">
            <Input
              className="flex-grow mr-4"
              placeholder="Type a message..."
              value={message}
              onChange={e => setMessage(e.target.value)}
              onKeyDown={handleKeyDown}
            />
            <button onClick={sendMessage} className="bg-main text-white px-4 py-2 rounded-lg">Send</button>
          </div>
        )}
      </div>

      {/* Edit Profile Modal */}
      {showEditProfile && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-lg p-6 w-96">
            <h2 className="text-xl font-semibold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <label className="block mb-2">Full Name:</label>
              <Input
                type="text"
                value={newName}
                onChange={e => setNewName(e.target.value)}
                className="w-full mb-4"
              />
              <label className="block mb-2">Profile Picture:</label>
              <Input
                type="file"
                accept="image/*"
                onChange={e => setNewProfilePic(e.target.files[0])}
                className="w-full mb-4"
              />
              <div className="flex justify-end">
                <button type="button" onClick={() => setShowEditProfile(false)} className="bg-gray-300 text-black px-4 py-2 rounded-lg mr-2">Cancel</button>
                <button type="submit" className="bg-main text-white px-4 py-2 rounded-lg">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
