import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
  try {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
    setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
  } catch (error) {
    console.error('Failed to fetch messages:', error);
  }
};

const sendMessage = async () => {
  if (message.trim() === '') {
    return;
  }

  setMessage('');
  try {
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    });

    await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    });

    await fetchConversations();
    await fetchMessages(messages?.conversationId, messages?.receiver);
  } catch (error) {
    console.error('Failed to send message:', error);
  }
};


  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
	localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
	e.preventDefault();
	let profilePicUrl = user?.profilePic;

	if (newProfilePic) {
	  const formData = new FormData();
	  formData.append('file', newProfilePic);
	  formData.append('upload_preset', 'imarj7');

	  try {
		const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
		profilePicUrl = res.data.secure_url;

		const updatedUser = {
		  ...user,
		  fullName: newName,
		  profilePic: profilePicUrl,
		};

		await fetch(`http://localhost:8000/api/users/${user?.id}`, {
		  method: 'PUT',
		  headers: {
			'Content-Type': 'application/json',
		  },
		  body: JSON.stringify(updatedUser),
		});

		localStorage.setItem('user:detail', JSON.stringify(updatedUser));
		setUser(updatedUser);
		setShowEditProfile(false);
	  } catch (error) {
		console.error('Error uploading or updating profile picture:', error);
	  }
	} else {
	  const updatedUser = {
		...user,
		fullName: newName,
	  };

	  await fetch(`http://localhost:8000/api/users/${user?.id}`, {
		method: 'PUT',
		headers: {
		  'Content-Type': 'application/json',
		},
		body: JSON.stringify(updatedUser),
	  });

	  localStorage.setItem('user:detail', JSON.stringify(updatedUser));
	  setUser(updatedUser);
	  setShowEditProfile(false);
	}
  };

  return (
    <div className="w-screen h-screen p-8 flex glass-effect">
      <div className="w-[25%] h-full bg-secondary overflow-scroll glass-effect p-6">
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center my-10 cursor-pointer hover:bg-hover p-4 rounded-md" key={conversationId}
                    onClick={() => fetchMessages(conversationId, { receiverId: user?.id, receiverName: user?.fullName })}>
                    <div><img src={user?.profilePic || pic} width={60} height={60} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
                    <div className="ml-6">
                      <h3 className="text-xl text-white">{user?.fullName}</h3>
                      {unseenCount > 0 && <p className="text-red-600 font-semibold">{unseenCount} new message(s)</p>}
                    </div>
                  </div>
                ))
                : <p className="text-lg text-white">No conversations yet.</p>
            }
          </div>
        </div>
      </div>

      <div className="w-[75%] h-full bg-white flex flex-col justify-between glass-effect p-6">
        {messages?.receiver ? (
          <div>
            <div className="flex items-center my-8 mx-10">
              <div><img src={messages?.receiver?.receiverPic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
              <div className="ml-6">
                <h3 className="text-xl text-black">{messages?.receiver?.receiverName}</h3>
              </div>
            </div>
            <hr />
            <div className="mx-7 mt-10 flex-grow overflow-scroll p-4">
              {messages?.messages.map((msg, index) => (
                <div className={`flex ${msg.user.id === user?.id ? 'justify-end' : 'justify-start'} my-2`} key={index}>
                  <div className={`px-4 py-2 ${msg.user.id === user?.id ? 'bg-blue-500 text-white' : 'bg-gray-300 text-black'} rounded-lg`}>
                    {msg.message}
                  </div>
                </div>
              ))}
              <div ref={messageRef}></div>
            </div>
            <div className="flex items-center my-8 mx-10">
              <Input value={message} onChange={setMessage} placeholder="Type your message..." onKeyDown={handleKeyDown} />
              <button onClick={sendMessage} className="ml-4 bg-blue-500 text-white px-4 py-2 rounded-lg">Send</button>
            </div>
          </div>
        ) : (
          <div className="flex flex-col items-center justify-center flex-grow">
            <h3 className="text-2xl text-gray-500">Select a conversation to start chatting</h3>
          </div>
        )}
      </div>

      {showEditProfile && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-8">
            <h2 className="text-2xl font-bold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="fullName">
                  Full Name
                </label>
                <input
                  type="text"
                  id="fullName"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="profilePic">
                  Profile Picture
                </label>
                <input
                  type="file"
                  id="profilePic"
                  onChange={(e) => setNewProfilePic(e.target.files[0])}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="flex items-center justify-between">
                <button
                  type="submit"
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Save
                </button>
                <button
                  type="button"
                  onClick={() => setShowEditProfile(false)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
