import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Button from "../../components/Button";
import Input from "../../components/Input";

const Form = ({ isSignInPage = false }) => {
    const [data, setData] = useState({
        ...(isSignInPage ? {} : { fullName: '' }),
        email: '',
        password: ''
    });

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`http://localhost:8000/api/${isSignInPage ? 'login' : 'register'}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            });

            const contentType = res.headers.get('Content-Type');
            let resData;

            if (contentType && contentType.includes('application/json')) {
                resData = await res.json();
            } else {
                resData = await res.text();
            }

            console.log('Response data:', resData);  // Log the response data

            if (!res.ok) {
                let errorText = typeof resData === 'string' ? resData : (resData.message || 'An error occurred');
                throw new Error(errorText);
            }

            if (typeof resData === 'string' && !isSignInPage && resData === 'User registered successfully') {
                toast.success('Successfully registered');
                setTimeout(() => {
                    navigate('/');
                }, 1500);
                return;
            }

            if (typeof resData === 'string') {
                throw new Error('Unexpected response format');
            }

            if (resData.token) {
                localStorage.setItem('user:token', resData.token);
                localStorage.setItem('user:detail', JSON.stringify(resData.user));

                toast.success(isSignInPage ? 'Successfully signed in' : 'Successfully registered');

                setTimeout(() => {
                    navigate('/');
                }, 1500);
            } else {
                throw new Error('No token received');
            }
        } catch (error) {
            console.error('Error:', error.message);
            toast.error(isSignInPage ? 'Sign in failed' : `Registration failed: ${error.message}`);
        }
    };

    return (
        <div className="h-screen flex items-center justify-center bg-white">
            <div className="w-full max-w-lg p-8 bg-white shadow-md rounded-md">
                <h2 className="text-2xl font-semibold mb-6 text-gray-800">{isSignInPage ? 'Holla, Welcome Back' : 'Sign Up'}</h2>
                <p className="mb-6 text-gray-600">{isSignInPage ? 'Hey, welcome back to your special place' : 'Sign up to create your account'}</p>
                <form className="flex flex-col" onSubmit={handleSubmit}>
                    {!isSignInPage && (
                        <Input
                            label="Full name"
                            name="fullName"
                            placeholder="Enter your full name"
                            className="mb-4"
                            inputClassName="p-3"
                            value={data.fullName}
                            onChange={(e) => setData({ ...data, fullName: e.target.value })}
                        />
                    )}
                    <Input
                        label="Email address"
                        type="email"
                        name="email"
                        placeholder="Enter your email"
                        className="mb-4"
                        inputClassName="p-3"
                        value={data.email}
                        onChange={(e) => setData({ ...data, email: e.target.value })}
                    />
                    <Input
                        label="Password"
                        type="password"
                        name="password"
                        placeholder="Enter your Password"
                        className="mb-4"
                        inputClassName="p-3"
                        value={data.password}
                        onChange={(e) => setData({ ...data, password: e.target.value })}
                    />
                    <Button label={isSignInPage ? 'Sign in' : 'Sign up'} type="submit" className="w-full mb-4 p-3 bg-blue-500 text-white rounded-md" />
                </form>
                <div className="text-center">
                    <p className="text-gray-600">
                        {isSignInPage ? "Don't have an account?" : "Already have an account?"} 
                        <span className="text-blue-500 cursor-pointer ml-1" onClick={() => navigate(`/users/${isSignInPage ? 'sign_up' : 'sign_in'}`)}>
                            {isSignInPage ? 'Sign up' : 'Sign in'}
                        </span>
                    </p>
                </div>
            </div>
            <ToastContainer />
        </div>
    );
};

export default Form;
