import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', (users) => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', (data) => {
        setMessages((prev) => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }],
        }));
        setConversations((prev) => {
          const existingConversation = prev.find((conv) => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter((conv) => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const resData = await res.json();

    // Filter out duplicates and initialize unseen counts
    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find((item) => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    // Store unique conversations in local storage
    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    // Load conversations from local storage if available
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    try {
      const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const resData = await res.json();
      setMessages({ messages: resData, receiver, conversationId });
      setConversations((prev) =>
        prev.map((conv) =>
          conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv
        )
      );
    } catch (error) {
      console.error('Failed to fetch messages:', error);
    }
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    try {
      socket?.emit('sendMessage', {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message,
        conversationId: messages?.conversationId,
      });

      await fetch(`http://localhost:8000/api/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          conversationId: messages?.conversationId,
          senderId: user?.id,
          message,
          receiverId: messages?.receiver?.receiverId,
        }),
      });

      // Refresh the dashboard
      await fetchConversations();
      await fetchMessages(messages?.conversationId, messages?.receiver);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
    localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    let profilePicUrl = user?.profilePic;

    if (newProfilePic) {
      const formData = new FormData();
      formData.append('file', newProfilePic);
      formData.append('upload_preset', 'imarj7');

      try {
        const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
        profilePicUrl = res.data.secure_url;
      } catch (error) {
        console.error('Error uploading profile picture:', error);
      }
    }

    const updatedUser = {
      ...user,
      fullName: newName,
      profilePic: profilePicUrl,
    };

    try {
      await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      });

      localStorage.setItem('user:detail', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowEditProfile(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div>
            <img
              src={user?.profilePic || pic}
              width={75}
              height={75}
              className="border border-primary p-[2px] rounded-full"
              alt="User Profile"
            />
          </div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">
              Edit Profile
            </button>
            <br />
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">
              Logout
            </button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {conversations.length > 0 ? (
              conversations.map(({ conversationId, user, unseenCount }) => (
                <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                  <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, user)}>
                    <div>
                      <img
                        src={user?.profilePic || pic}
                        className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
                        alt="User Profile"
                      />
                    </div>
                    <div className="ml-6">
                      <h3 className="text-lg text-white font-semibold">{user?.fullName}</h3>
                      <p className="text-sm text-white text-gray-600">{user?.email}</p>
                      {unseenCount > 0 && <div className="text-sm text-red-500">{unseenCount} unread messages</div>}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div>No messages yet.</div>
            )}
          </div>
        </div>
      </div>

      {/* Middle Section */}
      <div className="w-[50%] h-screen bg-primary flex flex-col justify-between">
        <div className="h-[10%] flex justify-between items-center px-5">
          <h1 className="text-3xl text-secondary">Messages</h1>
          <button
            className="bg-main px-4 py-2 text-white rounded"
            onClick={() => setShowFriends((prev) => !prev)}
          >
            {showFriends ? 'Hide Friends' : 'Show Friends'}
          </button>
        </div>
        <div className="h-[70%] overflow-scroll p-5">
          {messages?.messages?.length > 0 ? (
            messages?.messages?.map((msg, index) => (
              <div key={index} className={`flex ${msg.user === user?.id ? 'justify-end' : 'justify-start'}`}>
                <div
                  className={`bg-white p-2 my-2 rounded-md ${
                    msg.user === user?.id ? 'text-right' : 'text-left'
                  }`}
                >
                  <p className="text-black">{msg.message}</p>
                </div>
              </div>
            ))
          ) : (
            <div>No messages yet.</div>
          )}
          <div ref={messageRef}></div>
        </div>
        <div className="h-[20%] flex items-center px-5">
          <Input
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <button onClick={sendMessage} className="bg-main px-4 py-2 text-white rounded ml-2">
            Send
          </button>
        </div>
      </div>

      {/* Right Section */}
      {showFriends && (
        <div className="w-[25%] h-screen bg-secondary overflow-scroll">
          <div className="text-main text-lg font-semibold mx-7 mt-10">Friends</div>
          <div>
            {users?.length > 0 ? (
              users?.map((user) => (
                <div className="flex items-center py-6 border-b border-b-gray-300 mx-7" key={user._id}>
                  <div className="cursor-pointer flex items-center">
                    <div>
                      <img
                        src={user?.profilePic || pic}
                        className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
                        alt="User Profile"
                      />
                    </div>
                    <div className="ml-6">
                      <h3 className="text-lg text-white font-semibold">{user?.fullName}</h3>
                      <p className="text-sm text-white text-gray-600">{user?.email}</p>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div>No friends yet.</div>
            )}
          </div>
        </div>
      )}

      {/* Edit Profile Modal */}
      {showEditProfile && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-8 rounded-md w-[400px]">
            <h2 className="text-2xl font-semibold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-gray-700">Full Name</label>
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="w-full px-3 py-2 border rounded"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Profile Picture</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setNewProfilePic(e.target.files[0])}
                  className="w-full px-3 py-2 border rounded"
                />
              </div>
              <button type="submit" className="bg-main px-4 py-2 text-white rounded">
                Update
              </button>
              <button
                type="button"
                onClick={() => setShowEditProfile(false)}
                className="ml-2 bg-gray-500 px-4 py-2 text-white rounded"
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
