import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for HTTP requests

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const [initialLoad, setInitialLoad] = useState(true); // New state to track initial load
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    // Filter out duplicates and initialize unseen counts
    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    // Store unique conversations in local storage
    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    // Only set conversations if it's not the initial load
    if (!initialLoad) {
      setConversations(uniqueConversations);
    }
  };

  useEffect(() => {
    // Load conversations from local storage if available
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    try {
      const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setMessages({ messages: resData, receiver, conversationId });
      setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
    } catch (error) {
      console.error('Failed to fetch messages:', error);
    }
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    try {
      socket?.emit('sendMessage', {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message,
        conversationId: messages?.conversationId
      });

      await fetch(`http://localhost:8000/api/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          conversationId: messages?.conversationId,
          senderId: user?.id,
          message,
          receiverId: messages?.receiver?.receiverId
        })
      });

      // Refresh the dashboard
      await fetchConversations();
      await fetchMessages(messages?.conversationId, messages?.receiver);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
    localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    let profilePicUrl = user?.profilePic;

    if (newProfilePic) {
      const formData = new FormData();
      formData.append('file', newProfilePic);
      formData.append('upload_preset', 'imarj7');

      try {
        const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
        profilePicUrl = res.data.secure_url;

        // Update user with new profile picture URL
        const updatedUser = {
          ...user,
          fullName: newName,
          profilePic: profilePicUrl,
        };

        // Update user info in backend (assuming there's an endpoint for that)
        await fetch(`http://localhost:8000/api/users/${user?.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedUser),
        });

        // Update local storage and state
        localStorage.setItem('user:detail', JSON.stringify(updatedUser));
        setUser(updatedUser);
        setShowEditProfile(false);
      } catch (error) {
        console.error('Error uploading or updating profile picture:', error);
      }
    } else {
      // Handle case where no new profile picture was selected
      const updatedUser = {
        ...user,
        fullName: newName,
      };

      // Update user info in backend (assuming there's an endpoint for that)
      await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      });

      // Update local storage and state
      localStorage.setItem('user:detail', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowEditProfile(false);
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          {conversations.length === 0 ? (
            <p className="text-main">No conversations to show</p>
          ) : (
            conversations.map(({ conversationId, user, unseenCount }) => (
              <div key={conversationId} onClick={() => fetchMessages(conversationId, user)} className="flex items-center my-6 p-2 cursor-pointer rounded-lg">
                <img src={user?.profilePic || pic} width={50} height={50} className="rounded-full" alt="User" />
                <div className="ml-4">
                  <h3 className="text-md text-main font-semibold">{user?.fullName}</h3>
                  {unseenCount > 0 && <span className="text-sm text-red-600">{unseenCount} new messages</span>}
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Right Section */}
      <div className="w-[75%] h-screen bg-main flex flex-col">
        {/* Messages Section */}
        <div className="flex-1 p-6 overflow-y-scroll">
          {messages?.messages?.length ? messages?.messages?.map((msg, index) => (
            <div key={index} ref={messageRef} className={`my-2 p-3 rounded-lg ${msg.user.id === user?.id ? 'bg-blue-500 text-white self-end' : 'bg-gray-200 text-black self-start'}`}>
              <span>{msg.message}</span>
            </div>
          )) : (
            <div className="text-center text-gray-600">No messages yet.</div>
          )}
        </div>

        {/* Input Section */}
        <div className="flex items-center p-6 bg-secondary">
          <Input value={message} onChange={(e) => setMessage(e.target.value)} onKeyDown={handleKeyDown} placeholder="Type a message..." className="flex-1 mr-4" />
          <button onClick={sendMessage} className="px-6 py-2 bg-primary text-white rounded-lg">Send</button>
        </div>
      </div>

      {/* Edit Profile Modal */}
      {showEditProfile && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-8 rounded-lg">
            <h2 className="text-2xl font-bold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <label className="block mb-2">Name:</label>
              <input type="text" value={newName} onChange={(e) => setNewName(e.target.value)} className="w-full p-2 border border-gray-300 rounded-lg mb-4" />
              <label className="block mb-2">Profile Picture:</label>
              <input type="file" onChange={(e) => setNewProfilePic(e.target.files[0])} className="w-full p-2 border border-gray-300 rounded-lg mb-4" />
              <div className="flex justify-end">
                <button type="button" onClick={() => setShowEditProfile(false)} className="px-4 py-2 mr-2 bg-gray-300 text-gray-800 rounded-lg">Cancel</button>
                <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded-lg">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
