import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Cloudinary } from '@cloudinary/url-gen';
import { auto } from '@cloudinary/url-gen/actions/resize';
import { autoGravity } from '@cloudinary/url-gen/qualifiers/gravity';
import { AdvancedImage } from '@cloudinary/react';

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    // Filter out duplicates and initialize unseen counts
    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    // Store unique conversations in local storage
    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    // Load conversations from local storage if available
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
    setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    });
    await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    });

    // Refresh the dashboard
    fetchConversations();
    fetchMessages(messages?.conversationId, messages?.receiver);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
	localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    let profilePicUrl = user?.profilePic;

    if (newProfilePic) {
      const formData = new FormData();
      formData.append('file', newProfilePic);
      formData.append('upload_preset', 'imarj7');

      try {
        const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
        profilePicUrl = res.data.secure_url;
      } catch (error) {
        console.error('Error uploading profile picture:', error);
        return;
      }
    }

    const updatedUser = {
      ...user,
      fullName: newName,
      profilePic: profilePicUrl,
    };

    try {
      await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      });

      localStorage.setItem('user:detail', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowEditProfile(false);
    } catch (error) {
      console.error('Error updating user profile:', error);
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div>
            <img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" />
          </div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, user)}>
                      <div>
                        <img src={user?.profilePic || pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="User Profile" />
                      </div>
                      <div className="ml-6">
                        <h3 className="text-lg text-white font-semibold">{user?.fullName}</h3>
                        <p className="text-sm text-white text-gray-600">{user?.email}</p>
                        {unseenCount > 0 && <div className="text-sm text-red-500">{unseenCount} new messages</div>}
                      </div>
                    </div>
                  </div>
                )) : <div className="text-center text-white text-lg font-semibold mt-[90%]">No conversations</div>
            }
          </div>
        </div>
      </div>
      {/* Middle Section */}
      <div className="w-[50%] h-screen bg-main flex flex-col">
        <div className="bg-main flex-grow overflow-scroll">
          {messages?.messages?.map(({ user, message }, index) => (
            <div key={index} ref={messageRef} className={`w-full flex items-center p-8 ${user?.id === user?.id ? 'justify-end' : ''}`}>
              <div>
                <div className={`w-[300px] p-4 rounded ${user?.id === user?.id ? 'bg-primary text-white' : 'bg-secondary text-main'}`}>
                  {message}
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="w-full flex items-center">
          <Input
            onKeyDown={handleKeyDown}
            placeholder="Enter your message..."
            onChange={(e) => setMessage(e.target.value)}
            value={message}
            containerClassName="m-10 h-[55px]"
          />
          <button className="w-[20%] p-4 bg-secondary text-white text-lg rounded" onClick={sendMessage}>
            Send
          </button>
        </div>
      </div>
      {/* Right Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="mx-7 mt-20">
          <div className="flex justify-between items-center">
            <div className="text-main text-lg font-semibold">Friends</div>
            <div className="cursor-pointer text-primary text-lg" onClick={() => setShowFriends(!showFriends)}>
              <i className="fas fa-users"></i>
            </div>
          </div>
          {showFriends && (
            <div>
              {users.map(({ id, email, fullName, profilePic }) => (
                <div className="flex items-center py-6 border-b border-b-gray-300" key={id}>
                  <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(id, { receiverId: id, email, fullName, profilePic })}>
                    <div>
                      <img src={profilePic || pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="Friend Profile" />
                    </div>
                    <div className="ml-6">
                      <h3 className="text-lg text-white font-semibold">{fullName}</h3>
                      <p className="text-sm text-white text-gray-600">{email}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {showEditProfile && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg w-96">
            <h2 className="text-2xl font-semibold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Full Name</label>
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary focus:border-primary"
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Profile Picture</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setNewProfilePic(e.target.files[0])}
                  className="mt-1 block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 focus:outline-none"
                />
              </div>
              <div className="flex justify-end">
                <button
                  type="button"
                  onClick={() => setShowEditProfile(false)}
                  className="mr-4 bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="bg-primary text-white px-4 py-2 rounded-md hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary"
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
