import { Navigate } from 'react-router-dom';

const ProtectedRoute = ({ children, auth = false }) => {
  const isLoggedIn = localStorage.getItem('user:token') !== null;

  if (auth && !isLoggedIn) {
    // Redirect to sign-in page if authentication is required and user is not logged in
    return <Navigate to="/users/sign_in" replace />;
  } else if (!auth && isLoggedIn) {
    // Redirect to dashboard if no authentication is required and user is logged in
    return <Navigate to="/" replace />;
  }

  // Render the children components if authentication checks pass
  return children;
};

export default ProtectedRoute;
