import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Button from "../../components/Button";
import Input from "../../components/Input";

const Form = ({ isSignInPage = false }) => {
    const [data, setData] = useState({
        ...(isSignInPage ? {} : { fullName: '' }), 
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);

    const navigate = useNavigate();
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch(`http://localhost:8000/api/${isSignInPage ? 'login' : 'register'}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            });

            const contentType = res.headers.get('Content-Type');
            let resData;

            if (contentType && contentType.includes('application/json')) {
                resData = await res.json();
            } else {
                resData = await res.text();
            }

            console.log('Response data:', resData);  // Log the response data

            if (!res.ok) {
                let errorText = typeof resData === 'string' ? resData : (resData.message || 'An error occurred');
                throw new Error(errorText);
            }

            if (typeof resData === 'string' && !isSignInPage && resData === 'User registered successfully') {
                toast.success('Successfully registered');
                setTimeout(() => {
                    navigate('/');
                }, 1500);
                return;
            }

            if (typeof resData === 'string') {
                throw new Error('Unexpected response format');
            }

            if (resData.token) {
                localStorage.setItem('user:token', resData.token);
                localStorage.setItem('user:detail', JSON.stringify(resData.user));

                toast.success(isSignInPage ? 'Successfully signed in' : 'Successfully registered');

                setTimeout(() => {
                    navigate('/');
                }, 1500);
            } else {
                throw new Error('No token received');
            }
        } catch (error) {
            console.error('Error:', error.message);
            toast.error(isSignInPage ? 'Sign in failed' : `Registration failed: ${error.message}`);
        }
    };



    return (
        <div className="bg-[#bbd0ff] h-screen flex items-center justify-center relative">
            <div className="bg-[#1d3354] w-[450px] h-[500px] shadow-lg rounded-[10%] flex flex-col justify-center items-center">
                <div className="text-4xl text-white font-extrabold">Welcome {isSignInPage ? 'Back' : ''}</div>
                <div className="text-xl font-light mb-10 text-white">{isSignInPage ? 'Sign in to get explored' : 'Sign up to get started'}</div>
                <form className="flex flex-col items-center w-full text-white" onSubmit={handleSubmit}>
                    {!isSignInPage && (
                        <Input
                            label="Full name"
                            name="name"
                            placeholder="Enter your full name"
                            className="mb-6 w-[75%] "
                            value={data.fullName}
                            onChange={(e) => setData({ ...data, fullName: e.target.value })}
                        />
                    )}
                    <Input
                        label="Email address"
                        type="email"
                        name="email"
                        placeholder="Enter your email"
                        className="mb-6 w-[75%]"
                        value={data.email}
                        onChange={(e) => setData({ ...data, email: e.target.value })}
                    />
                    <Input
                        label="Password"
                        type={showPassword ? 'text' : 'password'}
                        name="password"
                        placeholder="Enter your Password"
                        className="mb-6 w-[75%]"
                        value={data.password}
                        onChange={(e) => setData({ ...data, password: e.target.value })}
                        showPasswordToggle
                        showPassword={showPassword}
                        onTogglePassword={() => setShowPassword(!showPassword)}
                    />
                    <Button label={isSignInPage ? 'Sign in' : 'Sign up'} type="submit" className="w-[75%] mb-2" />
                </form>
                <div>{isSignInPage ? "Don't have an account?" : "Already have an account?"} <span className="text-primary cursor-pointer underline" onClick={() => navigate(`/users/${isSignInPage ? 'sign_up' : 'sign_in'}`)}>{isSignInPage ? 'Sign up' : 'Sign in'}</span></div>
            </div>
            <ToastContainer />
        </div>
    );
};

export default Form;
