import React, { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input/index';
import Button from '../../components/Button';
import { io } from 'socket.io-client';
import './Dashboard.css'; // Assuming you have a CSS file for Dashboard styles

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [friends, setFriends] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false); // State to toggle friend section visibility
  const [friendSearch, setFriendSearch] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const messageRef = useRef(null);

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('Active users:', users);
      });
      socket.on('getMessage', data => {
        setMessages(prevMessages => [...prevMessages, { user: data.user, message: data.message }]);
      });
    }
    return () => {
      socket?.disconnect();
    };
  }, [socket, user]);

  useEffect(() => {
    messageRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    const fetchConversations = async () => {
      const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
      const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`);
      if (res.ok) {
        const data = await res.json();
        const uniqueConversations = data.reduce((acc, current) => {
          const existing = acc.find(item => item.user.email === current.user.email);
          if (!existing) {
            acc.push(current);
          }
          return acc;
        }, []);
        localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));
        setConversations(uniqueConversations);
      } else {
        console.error('Failed to fetch conversations');
      }
    };

    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchFriends = async () => {
      const res = await fetch(`http://localhost:8000/api/friends/${user?.id}`);
      if (res.ok) {
        const data = await res.json();
        setFriends(data);
      } else {
        console.error('Failed to fetch friends');
      }
    };
    fetchFriends();
  }, [user]);

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`);
    if (res.ok) {
      const data = await res.json();
      setMessages(data);
    } else {
      console.error('Failed to fetch messages');
    }
  };

  const sendMessage = async () => {
    if (!message.trim()) {
      return;
    }

    try {
      setMessage('');
      socket?.emit('sendMessage', {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message,
        conversationId: messages?.conversationId
      });
      await fetch(`http://localhost:8000/api/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          conversationId: messages?.conversationId,
          senderId: user?.id,
          message,
          receiverId: messages?.receiver?.receiverId
        })
      });
      fetchConversations();
      fetchMessages(messages?.conversationId, messages?.receiver);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleSearchChange = (e) => {
    setFriendSearch(e.target.value);
  };

  const searchFriend = async () => {
    try {
      const res = await fetch(`http://localhost:8000/api/users/search?email=${friendSearch}`);
      if (res.ok) {
        const data = await res.json();
        setSearchResult(data);
      } else {
        console.error('Search failed:', res.statusText);
        setSearchResult(null);
        alert('Search failed: ' + res.statusText);
      }
    } catch (error) {
      console.error('Error searching friend:', error);
    }
  };

  const addFriend = async (friend) => {
    try {
      const res = await fetch(`http://localhost:8000/api/users/add`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId: user?.id, friendId: friend.id })
      });
      if (res.ok) {
        setFriends([...friends, friend]);
        setSearchResult(null);
        setFriendSearch('');
      } else {
        console.error('Failed to add friend:', res.statusText);
        alert('Failed to add friend: ' + res.statusText);
      }
    } catch (error) {
      console.error('Error adding friend:', error);
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div><img src={pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
          </div>
        </div>
        <hr />
        <div className="mx-10 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, user)}>
                      <div><img src={pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="User Profile" /></div>
                      <div className="ml-6">
                        <h3 className="text-lg text-white font-semibold">{user?.fullName}</h3>
                        <p className="text-sm text-white text-gray-600">{user?.email}</p>
                      </div>
                    </div>
                  </div>
                )) : <div className="text-center text-white text-lg font-semibold mt-[90%]">No Conversations</div>
            }
          </div>
        </div>
      </div>

      {/* Middle Section */}
      <div className={`w-[73%] h-screen bg-center flex flex-col items-center`}>
        {
          messages?.receiver?.fullName &&
          <div className="w-[75%] bg-secondary h-[80px] my-14 rounded-full flex items-center px-14 py-2">
            <div className="cursor-pointer"><img src={pic} width={60} height={60} className="rounded-full" alt="Receiver Profile" /></div>
            <div className="ml-6 mr-auto">
              <h3 className="text-lg text-white font-semibold">{messages?.receiver?.fullName}</h3>
              <p className="text-sm font-light text-gray-600 text-white">{messages?.receiver?.email}</p>
            </div>
          </div>
        }
        <div className="h-[75%] w-full overflow-scroll shadow-sm">
          <div className="p-20">
            {
              messages?.length > 0 ?
                messages.map(({ message, user: { id } = {} }) => (
                  <div className={`max-w-[40%] rounded-b-xl p-4 mb-6 ${id === user?.id ? 'bg-primary text-white rounded-tl-xl ml-auto' : 'bg-secondary text-white rounded-tr-xl'} `} key={id}>{message}</div>
                )) : <div className="text-black text-center text-lg font-semibold mt-[50%]">No Conversation Selected</div>
            }
            <div ref={messageRef}></div>
          </div>
        </div>
        {
          messages?.receiver?.fullName &&
          <div className="p-14 w-full flex items-center">
            <Input
              placeholder="Type a message..."
              value={message}
              onKeyDown={handleKeyDown}
              onChange={(e) => setMessage(e.target.value)}
              className="w-[90%] text-white"
              inputClassName="p-4 border-0 shadow-md text-white font-semibold rounded-full bg-light focus:ring-0 focus:border-0 outline-none"
            />
            <div className={`ml-4 p-2 cursor-pointer bg-light rounded-full ${!message && 'pointer-events-none'}`} onClick={sendMessage}>
              <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-send" width="30" height="30" viewBox="0 0 24 24" strokeWidth="1.5" stroke="white" fill="none" strokeLinecap="round" strokeLinejoin="round">
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <line x1="10" y1="14" x2="21" y2="3" />
                <path d="M21 3l-6.5 18a0.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a0.55 .55 0 0 1 0 -1l18 -6.5" />
              </svg>
            </div>
          </div>
        }
      </div>

      {/* Toggle Friends Section */}
      <div className={`w-[2%] h-screen bg-light px-8 py-16 flex flex-col items-center justify-center`}>
        <button className="toggle-friends-button" onClick={() => setShowFriends(!showFriends)}>
          <svg xmlns="http://www.w3.org/2000/svg" className={`icon icon-tabler icon-tabler-chevron-${showFriends ? 'left' : 'right'}`} width="40" height="40" viewBox="0 0 24 24" strokeWidth="1.5" stroke="white" fill="none" strokeLinecap="round" strokeLinejoin="round">
            <path stroke="none" d="M0 0h24v24H0z" fill="none"/>
            <polyline points="15 6 9 12 15 18" />
          </svg>
          <div className="text-white">{showFriends ? 'Close' : 'Friend List'}</div>
        </button>
      </div>

      {/* Friend List Section */}
      <div className={`w-[25%] h-screen bg-light px-8 py-16 overflow-scroll fixed top-0 right-0 sidebar ${showFriends ? 'open' : ''}`}>
        <div className="flex justify-end">
          <button className="close-friends-button" onClick={() => setShowFriends(false)}>
            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-x" width="24" height="24" viewBox="0 0 24 24" strokeWidth="2" stroke="white" fill="none" strokeLinecap="round" strokeLinejoin="round">
              <path stroke="none" d="M0 0h24v24H0z" fill="none"/>
              <line x1="18" y1="6" x2="6" y2="18" />
              <line x1="6" y1="6" x2="18" y2="18" />
            </svg>
          </button>
        </div>
        <div className="text-main text-lg font-semibold">Friends</div>
        <div className="mt-4">
          <Input
            placeholder="Enter email to find friend"
            value={friendSearch}
            onChange={handleSearchChange}
            onKeyDown={(e) => e.key === 'Enter' && searchFriend()}
            className="w-full text-black"
            inputClassName="p-4 border-0 shadow-md font-semibold rounded-full bg-light focus:ring-0 focus:border-0 outline-none"
          />
          <button className="mt-2 p-2 bg-primary text-white rounded-full" onClick={searchFriend}>Search</button>
          {searchResult && (
            <div className="flex items-center py-6 border-b border-b-gray-300 mt-4">
              <div className="cursor-pointer flex items-center">
                <div><img src={pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="Friend Profile" /></div>
                <div className="ml-6">
                  <h3 className="text-lg font-semibold text-black">{searchResult.fullName}</h3>
                  <p className="text-sm font-light text-gray-600">{searchResult.email}</p>
                </div>
                <button className="ml-4 p-2 bg-primary text-white rounded-full" onClick={() => addFriend(searchResult)}>Add</button>
              </div>
            </div>
          )}
        </div>
        <div>
          {
            friends.length > 0 ?
              friends.map(({ id, fullName, email }) => (
                <div className="flex items-center py-8 border-b border-b-gray-300" key={id}>
                  <div className="cursor-pointer flex items-center" onClick={() => fetchMessages('new', { receiverId: id, fullName, email })}>
                    <div><img src={pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="Friend Profile" /></div>
                    <div className="ml-6">
                      <h3 className="text-lg font-semibold text-white">{fullName}</h3>
                      <p className="text-sm font-light text-white text-gray-600">{email}</p>
                    </div>
                  </div>
                </div>
              )) : <div className="text-center text-white text-lg font-semibold mt-[90%]">No Friends</div>
          }
        </div>
      </div>

    </div>
  );
};

export default Dashboard;
