import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for HTTP requests

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    // Filter out duplicates and initialize unseen counts
    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    // Store unique conversations in local storage
    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    // Load conversations from local storage if available
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
  try {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
    setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
  } catch (error) {
    console.error('Failed to fetch messages:', error);
  }
};

const sendMessage = async () => {
  if (message.trim() === '') {
    return;
  }

  setMessage('');
  try {
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    });

    await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    });

    // Refresh the dashboard
    await fetchConversations();
    await fetchMessages(messages?.conversationId, messages?.receiver);
  } catch (error) {
    console.error('Failed to send message:', error);
  }
};


  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    // localStorage.removeItem('user:detail');
	localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
	e.preventDefault();
	let profilePicUrl = user?.profilePic;

	if (newProfilePic) {
	  const formData = new FormData();
	  formData.append('file', newProfilePic);
	  formData.append('upload_preset', 'imarj7');

	  try {
		const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
		profilePicUrl = res.data.secure_url;

		// Update user with new profile picture URL
		const updatedUser = {
		  ...user,
		  fullName: newName,
		  profilePic: profilePicUrl,
		};

		// Update user info in backend (assuming there's an endpoint for that)
		await fetch(`http://localhost:8000/api/users/${user?.id}`, {
		  method: 'PUT',
		  headers: {
			'Content-Type': 'application/json',
		  },
		  body: JSON.stringify(updatedUser),
		});

		// Update local storage and state
		localStorage.setItem('user:detail', JSON.stringify(updatedUser));
		setUser(updatedUser);
		setShowEditProfile(false);
	  } catch (error) {
		console.error('Error uploading or updating profile picture:', error);
	  }
	} else {
	  // Handle case where no new profile picture was selected
	  const updatedUser = {
		...user,
		fullName: newName,
	  };

	  // Update user info in backend (assuming there's an endpoint for that)
	  await fetch(`http://localhost:8000/api/users/${user?.id}`, {
		method: 'PUT',
		headers: {
		  'Content-Type': 'application/json',
		},
		body: JSON.stringify(updatedUser),
	  });

	  // Update local storage and state
	  localStorage.setItem('user:detail', JSON.stringify(updatedUser));
	  setUser(updatedUser);
	  setShowEditProfile(false);
	}
  };

  return (
    <div className="w-screen flex p-4">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll p-4">
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, user)}>
                      <img src={user?.profilePic || pic} width={50} height={50} className="rounded-full" alt="User Profile" />
                      <div className="ml-4">
                        <h3 className="text-lg text-white">{user?.fullName}</h3>
                        {unseenCount > 0 && <p className="text-sm text-red-600">{unseenCount} new messages</p>}
                      </div>
                    </div>
                  </div>
                )) :
                <div className="text-lg text-white">No conversations yet</div>
            }
          </div>
        </div>
      </div>
      {/* Center Section */}
      <div className="w-[50%] bg-[#FBFBFB] h-screen overflow-scroll p-4">
        <div className="text-center mt-8">
          <h3 className="text-2xl text-secondary font-bold">{messages?.receiver?.user?.fullName}</h3>
          <hr />
        </div>
        <div className="my-8">
          {
            messages?.messages?.length > 0 ?
              messages?.messages?.map(({ user, message }, index) => (
                <div className={`flex ${user?.id === messages?.receiver?.receiverId ? 'justify-start' : 'justify-end'}`} key={index}>
                  <div className={`${user?.id === messages?.receiver?.receiverId ? 'bg-[#E8F0F8]' : 'bg-secondary text-white'} p-2 my-4 rounded-lg max-w-[70%]`}>
                    {message}
                  </div>
                </div>
              )) :
              <div className="text-center text-lg text-gray-600">No messages yet</div>
          }
          <div ref={messageRef}></div>
        </div>
        <div className="flex justify-between items-center">
          <input 
            type="text" 
            className="w-full py-3 px-4 border border-gray-300 rounded-lg mr-4" 
            placeholder="Type a message" 
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <button 
            className="py-3 px-6 bg-secondary text-white rounded-lg" 
            onClick={sendMessage}
          >
            Send
          </button>
        </div>
      </div>
      {/* Right Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll p-4">
        <div className="flex justify-between items-center mt-10">
          <h3 className="text-lg text-white font-semibold">Friends</h3>
          <button onClick={() => setShowFriends(!showFriends)} className="text-sm font-semibold text-blue-600">{showFriends ? 'Hide Friends' : 'Show Friends'}</button>
        </div>
        {showFriends &&
          <div className="mt-8">
            {
              users.length > 0 ?
                users.map((user) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300 cursor-pointer" key={user?.id}>
                    <img src={user?.profilePic || pic} width={50} height={50} className="rounded-full" alt="User Profile" />
                    <div className="ml-4">
                      <h3 className="text-lg text-white">{user?.fullName}</h3>
                    </div>
                  </div>
                )) :
                <div className="text-lg text-white">No friends yet</div>
            }
          </div>
        }
      </div>

      {/* Edit Profile Modal */}
      {showEditProfile &&
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-8 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">Edit Profile</h2>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
                  Name
                </label>
                <input 
                  id="name"
                  type="text" 
                  className="w-full py-2 px-3 border border-gray-300 rounded-lg"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="profilePic">
                  Profile Picture
                </label>
                <input 
                  id="profilePic"
                  type="file" 
                  className="w-full py-2 px-3 border border-gray-300 rounded-lg"
                  onChange={(e) => setNewProfilePic(e.target.files[0])}
                />
              </div>
              <div className="flex justify-end">
                <button type="button" onClick={() => setShowEditProfile(false)} className="py-2 px-4 bg-red-600 text-white rounded-lg mr-4">
                  Cancel
                </button>
                <button type="submit" className="py-2 px-4 bg-secondary text-white rounded-lg">
                  Save Changes
                </button>
              </div>
            </form>
          </div>
        </div>
      }
    </div>
  );
};

export default Dashboard;
