import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for HTTP requests

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
    const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();

    // Filter out duplicates and initialize unseen counts
    const uniqueConversations = resData.reduce((acc, current) => {
      const x = acc.find(item => item.user.email === current.user.email);
      if (!x) {
        return acc.concat([{ ...current, unseenCount: 0 }]);
      } else {
        return acc;
      }
    }, []);

    // Store unique conversations in local storage
    localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));

    setConversations(uniqueConversations);
  };

  useEffect(() => {
    // Load conversations from local storage if available
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
    setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    });
    await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    });

    // Refresh the dashboard
    fetchConversations();
    fetchMessages(messages?.conversationId, messages?.receiver);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
	localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
	e.preventDefault();
	let profilePicUrl = user?.profilePic;

	if (newProfilePic) {
	  const formData = new FormData();
	  formData.append('file', newProfilePic);
	  formData.append('upload_preset', 'imarj7');

	  try {
		const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
		profilePicUrl = res.data.secure_url;

		// Update user with new profile picture URL
		const updatedUser = {
		  ...user,
		  fullName: newName,
		  profilePic: profilePicUrl,
		};

		// Update user info in backend (assuming there's an endpoint for that)
		await fetch(`http://localhost:8000/api/users/${user?.id}`, {
		  method: 'PUT',
		  headers: {
			'Content-Type': 'application/json',
		  },
		  body: JSON.stringify(updatedUser),
		});

		// Update local storage and state
		localStorage.setItem('user:detail', JSON.stringify(updatedUser));
		setUser(updatedUser);
		setShowEditProfile(false);
		fetchConversations(); // Refresh conversations after profile update
	  } catch (error) {
		console.error('Error uploading or updating profile picture:', error);
	  }
	} else {
	  // Handle case where no new profile picture was selected
	  const updatedUser = {
		...user,
		fullName: newName,
	  };

	  // Update user info in backend (assuming there's an endpoint for that)
	  await fetch(`http://localhost:8000/api/users/${user?.id}`, {
		method: 'PUT',
		headers: {
		  'Content-Type': 'application/json',
		},
		body: JSON.stringify(updatedUser),
	  });

	  // Update local storage and state
	  localStorage.setItem('user:detail', JSON.stringify(updatedUser));
	  setUser(updatedUser);
	  setShowEditProfile(false);
	  fetchConversations(); // Refresh conversations after profile update
	}
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br/>
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer" onClick={() => fetchMessages(conversationId, user)}>
                      <img src={user?.profilePic || pic} width={50} height={50} className="rounded-full" alt="User Profile" />
                    </div>
                    <div className="ml-6 cursor-pointer" onClick={() => fetchMessages(conversationId, user)}>
                      <h3 className="text-lg text-main">{user?.fullName}</h3>
                      <p className="text-sm text-gray-400">{user?.email}</p>
                    </div>
                    {unseenCount > 0 && (
                      <div className="ml-auto bg-red-600 text-white rounded-full w-6 h-6 flex items-center justify-center">{unseenCount}</div>
                    )}
                  </div>
                ))
                : <p>No conversations available.</p>
            }
          </div>
        </div>
        <div className="text-main text-lg font-semibold mt-10 ml-7">
          <button onClick={() => setShowFriends(true)}>Add friends</button>
        </div>
      </div>
      {/* Right Section */}
      <div className="w-[75%] h-screen">
        <div className="h-[90%] overflow-scroll">
          {
            messages?.receiver &&
            messages?.messages.map(({ user: messageUser, message }, i) => (
              <div ref={messageRef} key={i} className={`flex m-5 ${messageUser?.email === user?.email ? 'justify-end' : 'justify-start'}`}>
                <div className="w-[80%]">
                  <div className={`flex items-center ${messageUser?.email === user?.email ? 'justify-end flex-row-reverse' : ''}`}>
                    <img src={messageUser?.profilePic || pic} width={30} height={30} className="rounded-full" alt="User Profile" />
                    <span className="ml-3 text-xs text-gray-400">{messageUser?.fullName}</span>
                  </div>
                  <div className={`p-3 mt-3 text-sm ${messageUser?.email === user?.email ? 'bg-primary text-white rounded-l-lg rounded-br-lg' : 'bg-gray-200 rounded-r-lg rounded-bl-lg'}`}>
                    {message}
                  </div>
                </div>
              </div>
            ))
          }
        </div>
        <div className="h-[10%] mx-5 my-5 flex justify-between items-center">
          <Input
            placeholder='Type a message'
            className="w-[80%] bg-gray-100"
            onChange={e => setMessage(e.target.value)}
            value={message}
            onKeyDown={handleKeyDown}
          />
          <button className="text-white bg-secondary w-[15%] h-full rounded-lg" onClick={sendMessage}>Send</button>
        </div>
      </div>
      {showFriends && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white w-1/2 p-8 rounded-lg">
            <h2 className="text-xl font-semibold mb-4">Add Friends</h2>
            <button className="absolute top-4 right-4 text-gray-500" onClick={() => setShowFriends(false)}>Close</button>
            <div>
              {users.length > 0 ? users.map(({ _id, fullName, email, profilePic }) => (
                <div key={_id} className="flex items-center py-2 border-b border-b-gray-300">
                  <div className="cursor-pointer">
                    <img src={profilePic || pic} width={50} height={50} className="rounded-full" alt="User Profile" />
                  </div>
                  <div className="ml-6">
                    <h3 className="text-lg text-main">{fullName}</h3>
                    <p className="text-sm text-gray-400">{email}</p>
                  </div>
                  <button className="ml-auto text-blue-600" onClick={async () => {
                    // Call to API to add friend
                    await fetch(`http://localhost:8000/api/conversations`, {
                      method: 'POST',
                      headers: {
                        'Content-Type': 'application/json',
                      },
                      body: JSON.stringify({
                        senderId: user?.id,
                        receiverId: _id
                      })
                    });
                    fetchConversations(); // Refresh conversations after adding friend
                    setShowFriends(false);
                  }}>Add</button>
                </div>
              )) : <p>No users available.</p>}
            </div>
          </div>
        </div>
      )}
      {showEditProfile && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white w-1/3 p-8 rounded-lg">
            <h2 className="text-xl font-semibold mb-4">Edit Profile</h2>
            <button className="absolute top-4 right-4 text-gray-500" onClick={() => setShowEditProfile(false)}>Close</button>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">Name</label>
                <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="name" type="text" value={newName} onChange={e => setNewName(e.target.value)} />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="profilePic">Profile Picture</label>
                <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="profilePic" type="file" onChange={e => setNewProfilePic(e.target.files[0])} />
              </div>
              <div className="flex items-center justify-between">
                <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
