import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

import Button from "../../components/Button";
import Input from "../../components/Input";

const Form = ({ isSignInPage = false }) => {
    const [data, setData] = useState({
        ...(isSignInPage ? {} : { fullName: '', profilePic: null }), 
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);
    const [imagePreview, setImagePreview] = useState(null); // For displaying image preview

    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        let profilePicUrl = null;

        if (data.profilePic) {
            const formData = new FormData();
            formData.append('file', data.profilePic);
            formData.append('upload_preset', 'imarj7');

            try {
                const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
                profilePicUrl = res.data.secure_url;
            } catch (error) {
                console.error('Error uploading profile picture:', error);
                toast.error('Image upload failed');
                return;
            }
        }

        const userData = {
            ...data,
            profilePic: profilePicUrl
        };

        try {
            const res = await fetch(`http://localhost:8000/api/${isSignInPage ? 'login' : 'register'}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(userData)
            });

            const resData = await res.json();

            if (!res.ok) {
                throw new Error(resData.message || 'An error occurred');
            }

            if (resData.token) {
                localStorage.setItem('user:token', resData.token);
                localStorage.setItem('user:detail', JSON.stringify(resData.user));

                toast.success(isSignInPage ? 'Successfully signed in' : 'Successfully registered');

                setTimeout(() => {
                    navigate('/');
                }, 1500);
            } else {
                throw new Error('No token received');
            }
        } catch (error) {
            console.error('Error:', error.message);
            toast.error(isSignInPage ? 'Sign in failed' : `Registration failed: ${error.message}`);
        }
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setData({ ...data, profilePic: file });

        const reader = new FileReader();
        reader.onloadend = () => {
            setImagePreview(reader.result);
        };
        reader.readAsDataURL(file);
    };

    return (
        <div className="bg-main h-screen flex items-center justify-center relative">
            <div className="bg-white w-[450px] h-[600px] shadow-lg rounded-[10%] flex flex-col justify-center items-center">
                <div className="text-4xl font-extrabold">Welcome {isSignInPage ? 'Back' : ''}</div>
                <div className="text-xl font-light mb-10">{isSignInPage ? 'Sign in to get explored' : 'Sign up to get started'}</div>
                <form className="flex flex-col items-center w-full" onSubmit={handleSubmit}>
                    {!isSignInPage && (
                        <>
                            <Input
                                label="Full name"
                                name="name"
                                placeholder="Enter your full name"
                                className="mb-6 w-[75%]"
                                value={data.fullName}
                                onChange={(e) => setData({ ...data, fullName: e.target.value })}
                            />
                            <div className="mb-6 w-[75%]">
                                <label className="block text-gray-700">Profile Picture</label>
                                <input
                                    type="file"
                                    onChange={handleFileChange}
                                    className="w-full px-3 py-2 border rounded"
                                />
                                {imagePreview && <img src={imagePreview} alt="Profile Preview" className="mt-4 w-20 h-20 rounded-full" />}
                            </div>
                        </>
                    )}
                    <Input
                        label="Email address"
                        type="email"
                        name="email"
                        placeholder="Enter your email"
                        className="mb-6 w-[75%]"
                        value={data.email}
                        onChange={(e) => setData({ ...data, email: e.target.value })}
                    />
                    <Input
                        label="Password"
                        type={showPassword ? 'text' : 'password'}
                        name="password"
                        placeholder="Enter your Password"
                        className="mb-6 w-[75%]"
                        value={data.password}
                        onChange={(e) => setData({ ...data, password: e.target.value })}
                        showPasswordToggle
                        showPassword={showPassword}
                        onTogglePassword={() => setShowPassword(!showPassword)}
                    />
                    <Button label={isSignInPage ? 'Sign in' : 'Sign up'} type="submit" className="w-[75%] mb-2" />
                </form>
                <div>{isSignInPage ? "Don't have an account?" : "Already have an account?"} <span className="text-primary cursor-pointer underline" onClick={() => navigate(`/users/${isSignInPage ? 'sign_up' : 'sign_in'}`)}>{isSignInPage ? 'Sign up' : 'Sign in'}</span></div>
            </div>
            <ToastContainer />
        </div>
    );
};

export default Form;
