import { useEffect, useRef, useState } from 'react';
import pic from '../../assets/pic.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for HTTP requests

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const [showFriends, setShowFriends] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [newName, setNewName] = useState(user?.fullName || '');
  const [newProfilePic, setNewProfilePic] = useState(null);
  const messageRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit('addUser', user?.id);
      socket.on('getUsers', users => {
        console.log('activeUsers :>> ', users);
      });
      socket.on('getMessage', data => {
        setMessages(prev => ({
          ...prev,
          messages: [...prev.messages, { user: data.user, message: data.message }]
        }));
        setConversations(prev => {
          const existingConversation = prev.find(conv => conv.conversationId === data.conversationId);
          if (existingConversation) {
            existingConversation.unseenCount = (existingConversation.unseenCount || 0) + 1;
            return [existingConversation, ...prev.filter(conv => conv.conversationId !== data.conversationId)];
          } else {
            return prev;
          }
        });
      });
    }
  }, [socket, user?.id]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  const fetchConversations = async () => {
    try {
      const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
      const res = await axios.get(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
        headers: {
          'Content-Type': 'application/json',
        }
      });
      const uniqueConversations = res.data.reduce((acc, current) => {
        const x = acc.find(item => item.user.email === current.user.email);
        if (!x) {
          return acc.concat([{ ...current, unseenCount: 0 }]);
        } else {
          return acc;
        }
      }, []);

      localStorage.setItem('uniqueConversations', JSON.stringify(uniqueConversations));
      setConversations(uniqueConversations);
    } catch (error) {
      console.error('Failed to fetch conversations:', error);
    }
  };

  useEffect(() => {
    const storedConversations = JSON.parse(localStorage.getItem('uniqueConversations'));
    if (storedConversations) {
      setConversations(storedConversations);
    } else {
      fetchConversations();
    }
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get(`http://localhost:8000/api/users/${user?.id}`, {
          headers: {
            'Content-Type': 'application/json',
          }
        });
        setUsers(res.data);
      } catch (error) {
        console.error('Failed to fetch users:', error);
      }
    };
    fetchUsers();
  }, [user?.id]);

  const fetchMessages = async (conversationId, receiver) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/message/${conversationId}`, {
        params: {
          senderId: user?.id,
          receiverId: receiver?.receiverId
        },
        headers: {
          'Content-Type': 'application/json',
        }
      });
      setMessages({ messages: res.data, receiver, conversationId });
      setConversations(prev => prev.map(conv => conv.conversationId === conversationId ? { ...conv, unseenCount: 0 } : conv));
    } catch (error) {
      console.error('Failed to fetch messages:', error);
    }
  };

  const sendMessage = async () => {
    if (message.trim() === '') {
      return;
    }

    setMessage('');
    try {
      socket?.emit('sendMessage', {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message,
        conversationId: messages?.conversationId
      });

      await axios.post(`http://localhost:8000/api/message`, {
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      }, {
        headers: {
          'Content-Type': 'application/json',
        }
      });

      await fetchConversations();
      await fetchMessages(messages?.conversationId, messages?.receiver);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === 'Return' || e.keyCode === 13) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user:detail');
    localStorage.removeItem('user:token');
    navigate('/users/sign_in');
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    let profilePicUrl = user?.profilePic;

    if (newProfilePic) {
      const formData = new FormData();
      formData.append('file', newProfilePic);
      formData.append('upload_preset', 'imarj7');

      try {
        const res = await axios.post('https://api.cloudinary.com/v1_1/dxjetg2f2/image/upload', formData);
        profilePicUrl = res.data.secure_url;
      } catch (error) {
        console.error('Error uploading profile picture:', error);
        return;
      }
    }

    try {
      const updatedUser = {
        ...user,
        fullName: newName,
        profilePic: profilePicUrl,
      };

      await axios.put(`http://localhost:8000/api/users/${user?.id}`, updatedUser, {
        headers: {
          'Content-Type': 'application/json',
        }
      });

      localStorage.setItem('user:detail', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowEditProfile(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className="w-screen flex">
      {/* Left Section */}
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-10">
          <div><img src={user?.profilePic || pic} width={75} height={75} className="border border-primary p-[2px] rounded-full" alt="User Profile" /></div>
          <div className="ml-6">
            <h3 className="text-xl text-white">{user?.fullName}</h3>
            <p className="text-lg text-white">My Account</p>
            <button onClick={() => setShowEditProfile(true)} className="text-sm font-semibold text-blue-600">Edit Profile</button><br />
            <button onClick={handleLogout} className="text-sm font-semibold text-red-600">Logout</button>
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold">Messages</div>
          <div>
            {
              conversations.length > 0 ?
                conversations.map(({ conversationId, user, unseenCount }) => (
                  <div className="flex items-center py-6 border-b border-b-gray-300" key={conversationId}>
                    <div className="cursor-pointer flex items-center" onClick={() => fetchMessages(conversationId, user)}>
                      <div><img src={user?.profilePic || pic} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" alt="User Profile" /></div>
                      <div className="ml-6">
                        <h3 className="text-lg text-white font-semibold">{user?.fullName}</h3>
                        <p className="text-sm text-white text-gray-600">{user?.email}</p>
                        {unseenCount > 0 && <div className="text-sm text-red-500">{unseenCount} new messages</div>}
                      </div>
                    </div>
                  </div>
                ))
                :
                <div>No conversations found</div>
            }
          </div>
        </div>
        <hr />
        <div className="mx-7 mt-10">
          <div className="text-main text-lg font-semibold cursor-pointer" onClick={() => setShowFriends(true)}>Friends</div>
        </div>
      </div>

      {/* Right Section */}
      <div className="w-[75%] h-screen bg-primary">
        <div className="flex flex-col justify-between h-full">
          {/* Chat Area */}
          <div className="h-[80%] p-8 overflow-scroll">
            {messages?.receiver ? (
              <div>
                <div className="flex items-center mb-6">
                  <div><img src={messages?.receiver?.profilePic || pic} className="w-[50px] h-[50px] rounded-full p-[2px] border border-primary" alt="Receiver Profile" /></div>
                  <div className="ml-6">
                    <h3 className="text-lg text-white font-semibold">{messages?.receiver?.fullName}</h3>
                    <p className="text-sm text-white text-gray-600">{messages?.receiver?.email}</p>
                  </div>
                </div>
                <div>
                  {messages?.messages?.map(({ user, message }, index) => (
                    <div key={index} className={`mb-4 ${user?.id === messages?.receiver?.receiverId ? 'text-left' : 'text-right'}`}>
                      <div className={`inline-block p-4 rounded-lg ${user?.id === messages?.receiver?.receiverId ? 'bg-gray-700' : 'bg-blue-600'} text-white`}>
                        {message}
                      </div>
                    </div>
                  ))}
                  <div ref={messageRef} />
                </div>
              </div>
            ) : (
              <div className="text-center text-white">Select a conversation to start chatting</div>
            )}
          </div>

          {/* Message Input */}
          <div className="h-[20%] p-8 bg-secondary flex items-center justify-between">
            <Input
              type="text"
              value={message}
              placeholder="Type your message here..."
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              className="w-[80%] p-4 rounded-lg"
            />
            <button onClick={sendMessage} className="p-4 bg-blue-600 text-white rounded-lg">Send</button>
          </div>
        </div>
      </div>

      {/* Edit Profile Modal */}
      {showEditProfile && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg">
            <h3 className="text-xl font-semibold mb-6">Edit Profile</h3>
            <form onSubmit={handleProfileUpdate}>
              <div className="mb-4">
                <label className="block text-sm font-semibold mb-2">Full Name</label>
                <Input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="w-full p-4 rounded-lg border border-gray-300"
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-semibold mb-2">Profile Picture</label>
                <Input
                  type="file"
                  onChange={(e) => setNewProfilePic(e.target.files[0])}
                  className="w-full p-4 rounded-lg border border-gray-300"
                />
              </div>
              <div className="flex items-center justify-between">
                <button type="submit" className="p-4 bg-blue-600 text-white rounded-lg">Save</button>
                <button type="button" onClick={() => setShowEditProfile(false)} className="p-4 bg-gray-600 text-white rounded-lg">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Friends List Modal */}
      {showFriends && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg">
            <h3 className="text-xl font-semibold mb-6">Friends List</h3>
            <div>
              {users?.map(({ id, fullName, profilePic, email }) => (
                <div key={id} className="flex items-center mb-4">
                  <div><img src={profilePic || pic} className="w-[50px] h-[50px] rounded-full p-[2px] border border-primary" alt="Friend Profile" /></div>
                  <div className="ml-6">
                    <h3 className="text-lg font-semibold">{fullName}</h3>
                    <p className="text-sm text-gray-600">{email}</p>
                  </div>
                  <button onClick={() => setShowFriends(false)} className="ml-auto p-4 bg-blue-600 text-white rounded-lg">Close</button>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
