import { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from "../../components/Button";
import Input from "../../components/Input";

const Form = ({ isSignInPage = false }) => {
    const [data, setData] = useState({
        ...(!isSignInPage && {
            fullName: ''
        }),
        email: '',
        password: ''
    });
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting form with data:', data);

        const res = await fetch(`http://localhost:8000/api/${isSignInPage ? 'login' : 'register'}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        });

        console.log('Response status:', res.status);

        if (res.status === 400) {
            console.log('Invalid credentials');
            alert('Invalid credentials');
        } else {
            const resData = await res.json();
            if (resData.token) {
                localStorage.setItem('user:token', resData.token);
                localStorage.setItem('user:detail', JSON.stringify(resData.user));
                navigate('/');
                if (!isSignInPage) {
                    toast.success('Successfully registered', {
                        position: "bottom-right",
                        autoClose: 3000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });
                }
            }
        }
    };

    return (
        <div className="bg-main h-screen flex items-center justify-center">
            <div className="bg-white w-[450px] h-[500px] shadow-lg rounded-[10%] flex flex-col justify-center items-center">
                <div className="text-4xl font-extrabold">Welcome {isSignInPage ? 'Back' : ''}</div>
                <div className="text-xl font-light mb-10">{isSignInPage ? 'Sign in to get explored' : 'Sign up to get started'}</div>
                <form className="flex flex-col items-center w-full" onSubmit={handleSubmit}>
                    {!isSignInPage && <Input label="Full name" name="name" placeholder="Enter your full name" className="mb-6 w-[75%]" value={data.fullName} onChange={(e) => setData({ ...data, fullName: e.target.value })} />}
                    <Input label="Email address" type="email" name="email" placeholder="Enter your email" className="mb-6 w-[75%]" value={data.email} onChange={(e) => setData({ ...data, email: e.target.value })} />
                    <Input label="Password" type="password" name="password" placeholder="Enter your Password" className="mb-10 w-[75%]" value={data.password} onChange={(e) => setData({ ...data, password: e.target.value })} />
                    <Button label={isSignInPage ? 'Sign in' : 'Sign up'} type="submit" className="w-[75%] mb-2" />
                </form>
                <div>{isSignInPage ? "Don't have an account?" : "Already have an account?"} <span className="text-primary cursor-pointer underline" onClick={() => navigate(`/users/${isSignInPage ? 'sign_up' : 'sign_in'}`)}>{isSignInPage ? 'Sign up' : 'Sign in'}</span></div>
            </div>
            <ToastContainer />
        </div>
    );
};

export default Form;
